import React, { useState, useEffect, ChangeEvent } from 'react';
import { getArray, postArray } from '../../api/api';

import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';

import { useForm } from "react-hook-form";

const Chiropractors = () => {

    interface IChiropractors {
        chiropractorId: number;
        firstName: string;
        lastname: string;
        emailAddress: string;
        phoneNumber: number;
        timeCreated: Date;

        postData: string;
        data: any;
        values: any;
        errors: any;
    }

    const [chiropractorDetails, setchiropractorDetails] = useState<any[]>([]);

    

    useEffect(() => {
        const makeArrayRequest = async () => {
            setchiropractorDetails(await getArray());
        }
        makeArrayRequest();
    }, [])

    /*const [PostDetails, setPostDetails] = useState('');
    const handlePostDetailsChange = (x: any | null) => {
        setPostDetails(x);
    }
    const [HasFocus, setHasFocus] = useState<boolean>(false);

    const handleSubmit = () => {
        console.log(PostDetails);
    }*/
    
    const postArray1 = () => {
        const { handleSubmit, register, errors } = useForm<FormValues>();
        const onSubmit = values => console.log(values);
    }
    
    return (
        <>
            {chiropractorDetails.map(x => <li key={x.chiropractorId}> {x.firstName} {x.firstName} {x.emailAddress} {x.phoneNumber} {x.timeCreated}</li>)}

            <form onSubmit={handleSubmit(onSubmit)}>
                <input
                    name="firstName"
                    ref={register({
                        required: "Required",
                        pattern: {
                            value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                            message: "invalid email address"
                        }
                    })}
                />
                {errors.email && errors.email.message}

                <input
                    name="username"
                    ref={register({
                        validate: value => value !== "admin" || "Nice try!"
                    })}
                />
                {errors.username && errors.username.message}

                <button type="submit">Submit</button>
            </form>
        </>
    )
}

export default Chiropractors;
