import React, { useState, useEffect, ChangeEvent } from 'react';
import { getArray, postArray } from '../../api/api';
import { useForm, SubmitHandler } from "react-hook-form";

export const Chiropractors = () => {

    interface IChiropractors {
        chiropractorId: number;
        firstName: string;
        lastname: string;
        emailAddress: string;
        phoneNumber: number;
        timeCreated: Date;

        postArray: any;
        data: any;
        values: any;
        errors: any;
    }

    const [chiropractorDetails, setchiropractorDetails] = useState<any[]>([]);
    const [formData, setFormData] = useState<any[]>([]);

    const { handleSubmit, errors } = useForm<IChiropractors>();

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value })
        console.log(formData)
    }

    const onSubmit = (data: IChiropractors | {}) => {

        console.log("testing:", data);
        postArray({...data })


    }

    

 
    useEffect(() => {
        const makeArrayRequest = async () => {
            setchiropractorDetails(await getArray());
        }
        makeArrayRequest();
    }, [])

        return (
            <>
                {chiropractorDetails.map(x => <li key={x.chiropractorId}> {x.firstName} {x.firstName} {x.emailAddress} {x.phoneNumber} {x.timeCreated}</li>)}

                <form onSubmit={handleSubmit(onSubmit)}>
                    <input
                        type="text"
                        id="firstName"
                        name="firstName"
                        onChange={ handleChange}
                    />
                    {errors.firstName && errors.firstName.message}

                    <input
                        type="text"
                        id="lastName"
                        name="lastName"
                        onChange={handleChange}
                    />
                    {errors.lastname && errors.lastname.message}

                    <button type="submit" value="Submit">Submit</button>
                </form>
            </>
        )
}
    export default Chiropractors;
